import React, { useState, useEffect } from 'react';
import './Table.css';

function Table() {
    const [artistError, setArtistError] = useState(null);
    const [isArtistLoaded, setIsArtistLoaded] = useState(false);
    const [artistItems, setArtistItems] = useState([]);
    const [eventError, setEventError] = useState(null);
    const [isEventLoaded, setIsEventLoaded] = useState(false);
    const [eventItems, setEventItems] = useState([]);
    const [venueError, setVenueError] = useState(null);
    const [isVenueLoaded, setIsVenueLoaded] = useState(false);
    const [venueItems, setVenueItems] = useState([]);
    const [showVenue, setShowVenue] = useState(false); 
    const [venueInfo, setVenueInfo] = useState([]); 

    // This fetches the JSON data and builds the 
    useEffect(() => {
        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/artists.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsArtistLoaded(true);
                    setArtistItems(result);
                },
                (error) => {
                    setIsArtistLoaded(true);
                    setArtistError(error);
                }
        )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/events.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsEventLoaded(true);
                    setEventItems(result);
                },
                (error) => {
                    setIsEventLoaded(true);
                    setEventError(error);
                }
        )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/venues.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsVenueLoaded(true);
                    setVenueItems(result);
                },
                (error) => {
                    setIsVenueLoaded(true);
                    setVenueError(error);
                }
        )
        

    }, [])

    function displayVenue() {
        return (
            <div>
                {venueInfo.name}
                <img src={venueInfo.icon} alt={venueInfo.name} />
                {venueInfo.address}
            </div>)
    }

    function expandVenue(venueName, venueIcon, venueAddress)
    {
        setShowVenue(true); 
        setVenueInfo({
            name: venueName, 
            icon: venueIcon,
            address : venueAddress
        });         
     }

    function buildRow(item, index) {

        let rowID = "event_" + item.id; 

        let artistName = null; 
        let artistGenre = null; 

        //get artist and genre
        artistItems.forEach(element => {
            if (element.id == item.artist_id) {
                artistName = element.name; 
                artistGenre = element.genre; 
            }
        });

        let venueName = null; 
        let venueIcon = null; 
        let venueAddress = null;

        //get venue 
        venueItems.forEach(element => {
            if (element.id == item.venue_id) {
                venueName = element.name; 
                venueIcon = element.icon; 
                venueAddress = element.address; 
            }
        });
        return (
            <tr key={index} id="rowID">
                <th>{item.date}</th>
                <th>{item.title}</th>
                <th>{artistName}</th>
                <th>{artistGenre}</th>
                <th><button onclick={expandVenue(venueName, venueIcon, venueAddress) }>{venueName}</button></th>
                <th>${item.price}</th>
            </tr>
        )
    } 

    if (artistError || eventError || venueError) {
        return <div>Error: {artistError.message}</div>;
    } else if (!isArtistLoaded || !isEventLoaded || !isVenueLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <div className="Table-Header">
                <table >
                    <tr>
                        <th>Event Date</th>
                        <th>Event Name</th>
                        <th>Artist Name</th>
                        <th>Artist Genre</th>
                        <th>Venue</th>
                        <th>Ticket Price</th>
                    </tr>
                    {eventItems.map((item, index) => buildRow(item,index))};
                </table>
                { showVenue ? { expandVenue } : null}
            </div>


        );
    }
}

export default Table;
