import React, { useState, useEffect } from 'react';
import './Table.css';

function Table() {
    const [artistError, setArtistError] = useState(null);
    const [isArtistLoaded, setIsArtistLoaded] = useState(false);
    const [artistItems, setArtistItems] = useState([]);
    const [eventError, setEventError] = useState(null);
    const [isEventLoaded, setIsEventLoaded] = useState(false);
    const [EventItems, setEventItems] = useState([]);
    const [venueError, setVenueError] = useState(null);
    const [isVenueLoaded, setIsVenueLoaded] = useState(false);
    const [VenueItems, setVenueItems] = useState([]);

    // This fetches the JSON data and builds the 
    useEffect(() => {
        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/artists.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsArtistLoaded(true);
                    setArtistItems(result);
                },
                (error) => {
                    setIsArtistLoaded(true);
                    setArtistError(error);
                }
        )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/events.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsEventLoaded(true);
                    setEventItems(result);
                },
                (error) => {
                    setIsEventLoaded(true);
                    setEventError(error);
                }
        )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/venues.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsVenueLoaded(true);
                    setVenueItems(result);
                },
                (error) => {
                    setIsVenueLoaded(true);
                    setVenueError(error);
                }
            )
    }, [])

    if (artistError) {
        return <div>Error: {artistError.message}</div>;
    } else if (!isArtistLoaded) {
        return <div>Loading...</div>;
    } else {
        return (
            <div className="Table-Header">
                <table >
                    <tr>
                        <th>Event Date</th>
                        <th>Event Name</th>
                        <th>Artist Name</th>
                        <th>Artist Genre</th>
                        <th>Venue</th>
                        <th>Ticket Price</th>
                    </tr>

                        {artistItems.map((item, index) => (
                            <tr key={item.id}>
                            <th>{eventItems[index].date}</th>
                            <th>Blank</th>
                            <th>{item.name}</th>
                            <th>{item.genre}</th>
                            </tr>
                        ))}

                </table>
            </div>


        );
    }
}

export default Table;
