import React from 'react';
import './Table.css';

class Table extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            profile: [
                { eventDate: null }, 
                { eventName: null },
                { artistName: null }, 
                { artistGenre: null }, 
                { ventue: null }, 
                { ticketPrice: null},
            ],
            eventProfiles : [],
            eventError: null,
            isLoadedEvents: false,
            eventItems: [],
            venueError: null,
            isLoadedVenue: false,
            venueItems: [],
            artistError: null,
            isLoadedArtist: false,
            artistItems: [],
            size: 3,
        };
    }

    componentDidMount() {
        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/artists.json")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoadedArtist: true,
                        artistItems: result.items
                    });
                },
                (error) => {
                    this.setState({
                        isLoadedArtist: true,
                        artistError : "error loading artists"
                    });
                }
            )
    }

    render() {
        const { artistError, isLoadedArtist, artistItems } = this.state;
        if (artistError) {
            return <div>Error: {artistError.message}</div>;
        } else if (!isLoadedArtist) {
            return <div>Loading...</div>;
        } else {
            return (
                <div> artistItems loaded
                console.log(artistItems);
                </div>
            );
        }
    }       
}


export default Table;
