import React from 'react';
import './Table.css';

class Table extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            profile: [
                { eventDate: null }, 
                { eventName: null },
                { artistName: null }, 
                { artistGenre: null }, 
                { ventue: null }, 
                { ticketPrice: null},
            ],
            eventProfiles : [],
            eventError: null,
            isLoadedEvents: false,
            eventItems: [],
            venueError: null,
            isLoadedVenue: false,
            venueItems: [],
            artistError: null,
            isLoadedArtist: false,
            artistItems: [],
            size: 3,
        };
    }

    componentDidMount() {


        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/venues.json")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoadedVenue: true,
                        venueItem: result.items
                    })
                }
            )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/artists.json")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoadedArtists: true,
                        artistItems: result.items
                    })
                }
            )


    }

    render() {

        let rows = [];
        for (var i = 0; i < this.state.size; i++) {
            let rowID = `row${i}`
            let cell = []
            for (var idx = 0; idx < 6; idx++) {
                let cellID = `cell${i}-${idx}`
                cell.push(<td key={cellID} id={cellID}></td>)
            }
            rows.push(<tr key={i} id={rowID}>{cell}</tr>)
        }

        const { artistItems } = this.state;

        return (

            <div className="Table-Header">
            <table >
                <tr>
                    <th>Event Date</th>
                    <th>Event Name</th>
                    <th>Artist Name</th>
                    <th>Artist Genre</th>
                    <th>Venue</th>
                    <th>Ticket Price</th>
                </tr>
                <tbody>
                    {rows}
                </tbody>
                </table>

                <ul>
                    {artistItems.map(artistItems => (
                        <li key={artistItems.id}>
                            {artistItems.name} {artistItems.genre}
                        </li>
                    ))}
                </ul>

            </div>


        )
    }       
}


export default Table;
