import React, { useState, useEffect } from 'react';
import './Table.css';
import Row from './Row.js'; 

function Table() {
    const [artistError, setArtistError] = useState(null);
    const [isArtistLoaded, setIsArtistLoaded] = useState(false);
    const [artistItems, setArtistItems] = useState([]);
    const [eventError, setEventError] = useState(null);
    const [isEventLoaded, setIsEventLoaded] = useState(false);
    const [eventItems, setEventItems] = useState([]);
    const [venueError, setVenueError] = useState(null);
    const [isVenueLoaded, setIsVenueLoaded] = useState(false);
    const [venueItems, setVenueItems] = useState([]);
    const [optionSelected, setOptionSelected] = useState(null); 
   
    // This fetches the JSON data and builds the 
    useEffect(() => {
        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/artists.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsArtistLoaded(true);
                    setArtistItems(result);
                },
                (error) => {
                    setIsArtistLoaded(true);
                    setArtistError(error);
                }
        )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/events.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsEventLoaded(true);
                    setEventItems(result);
                },
                (error) => {
                    setIsEventLoaded(true);
                    setEventError(error);
                }
        )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/venues.json")
            .then(res => res.json())
            .then(
                (result) => {
                    setIsVenueLoaded(true);
                    setVenueItems(result);
                },
                (error) => {
                    setIsVenueLoaded(true);
                    setVenueError(error);
                }
        )
        

    }, [])

    // method keeps track of which radio button is selected
    function onRadioChange(event) {
        setOptionSelected(event.target.value); 
    }

    // if an error occured output corresponding error
    if (artistError || eventError || venueError) {
        if (artistError) {
            return <div>Error: {artistError.message}</div>;
        }
        else if (eventError) {
            return <div>Error: {eventError.message}</div>;
        }
        else {
            return <div>Error: {venueError.message}</div>;
        }
    // if one of the JSON files isn't loaded output loading message
    } else if (!isArtistLoaded || !isEventLoaded || !isVenueLoaded) {
        return <div>Loading...</div>;
    // All files loaded, no errors processing normally
    } else {
        return (
            <div className="Table-Header">
                Sort By: 
                <form>
                    <div className="radio">
                        <label>
                            <input
                                type="radio"
                                value="Artist"
                                name="sortRadio"
                                onChange={onRadioChange}
                            />
                            Artist
                        </label>
                    </div>
                    <div className="radio">
                        <label>
                            <input
                                type="radio"
                                value="Venue"
                                name="sortRadio"
                                onChange={onRadioChange}
                            />
                        Venue
                        </label>
                    </div>
                </form> 
                <table >
                    <tr>
                        <th>Event Date</th>
                        <th>Event Name</th>
                        <th>Artist Name</th>
                        <th>Artist Genre</th>
                        <th>Venue</th>
                        <th>Ticket Price</th>
                    </tr>
                    <Row />
                </table>                               
            </div>


        );
    }
}

export default Table;
