import React, { useState, useEffect } from 'react';
import './Table.css';
import Row from './Row.js'; 

class Table extends React.Component {
    constructor() {
        super();
        this.state = {
            artistError: null,
            isArtistLoaded: false,
            artistItems: [],
            eventError: null,
            isEventLoaded: false,
            eventItems: [],
            venueError: null,
            isVenueLoaded: null,
            venueItems: [],
            optionSelected: null,
        }
    }

    // This fetches the JSON data and builds the 
    componentDidMount() {
        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/artists.json")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isArtistLoaded: true,
                        artistItems: result.items,
                    });
                },
                (error) => {
                    this.setState({
                        isArtistLoaded: true,
                        artistError: error,
                    });
                }
            )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/events.json")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isEventLoaded: true,
                        eventItems: result.items,
                    });
                },
                (error) => {
                    this.setState({
                        isEventLoaded: true,
                        eventError: error,
                    });
                }
            )

        fetch("https://oll-coding-exercise.s3-us-west-2.amazonaws.com/venues.json")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isVenueLoaded: true,
                        venueItems: result.items,
                    });
                },
                (error) => {
                    this.setState({
                        isVenueLoaded: true,
                        venueError: error,
                    });
                }
            )


    }

    // method keeps track of which radio button is selected
    onRadioChange(event) {
        setOptionSelected(event.target.value);
    }

    render() {
        // if an error occured output corresponding error
        if (this.state.artistError || this.state.eventError || this.state.venueError) {

            if (this.state.artistError) {
                return <div>Error: {this.state.artistError.message}</div>;
            }
            else if (this.state.eventError) {
                return <div>Error: {this.state.eventError.message}</div>;
            }
            else {
                return <div>Error: {this.state.venueError.message}</div>;
            }
        }
        // if one of the JSON files isn't loaded output loading message
        else if (!this.state.isArtistLoaded || !this.state.isEventLoaded || !this.state.isVenueLoaded) {
            return <div>Loading...</div>;
        }
        // All files loaded, no errors processing normally
        else {
            return (
                <div className="Table-Header">
                    Sort By:
                    <form>
                        <div className="radio">
                            <label>
                                <input
                                    type="radio"
                                    value="Artist"
                                    name="sortRadio"
                                    onChange={onRadioChange}
                                />
                                Artist
                            </label>
                        </div>
                        <div className="radio">
                            <label>
                                <input
                                    type="radio"
                                    value="Venue"
                                    name="sortRadio"
                                    onChange={onRadioChange}
                                />
                                Venue
                            </label>
                        </div>
                    </form>
                    <table >
                        <tr>
                            <th>Event Date</th>
                            <th>Event Name</th>
                            <th>Artist Name</th>
                            <th>Artist Genre</th>
                            <th>Venue</th>
                            <th>Ticket Price</th>
                        </tr>
                        <Row />
                    </table>
                </div>
            );
        }
    }
}

export default Table;
